    private void residentTypeComboBoxItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_residentTypeComboBoxItemStateChanged
        if(residentTypeComboBox.getSelectedItem().equals("Tenant")){
            selectTenant(true);
        } else {
            selectTenant(false);
        }
    }//GEN-LAST:event_residentTypeComboBoxItemStateChanged

